pub enum ErrorKind {

    /// incompatible shape
    IncompatibleShape = ,

    /// incompatible memory layout
    IncompatibleLayout,

    /// the shape does not fit inside type limits
    RangeLimited,

    /// out of bounds indexing
    OutOfBounds,

    /// aliasing array elements
    Unsupported,

    /// overflow when computing offset, length, etc.
    Overflow,
}

pub enum SliceInfoElem {

    /// A range with step size. `end` is an exclusive index. Negative `begin`
    /// or `end` indexes are counted from the back of the axis. If `end` is
    /// `None`, the slice extends to the end of the axis.
    Slice {
        start: isize,
        end: Option<isize>,
        step: isize,
    },

    /// A single index.
    Index(isize),

    /// A new axis of length 1.
    NewAxis,
}

