pub enum FoldWhile<T> {

    /// Continue folding with this value
    Continue(T),

    /// Fold is complete and will return this value
    Done(T),
}

pub enum CowRepr<'a, A> {

    /// Borrowed data.
    View(ViewRepr<&'a A>),

    /// Owned data.
    Owned(OwnedRepr<A>),
}
